import React, { useEffect, useState } from "react";
import { countries } from "../../../../../api/countries"
import { Pie } from "react-chartjs-2"

const Chart = () => {

    const [countriesList, setCountriesList] = useState();

    useEffect(() => {
        countries.getCountries().then((response) => {
            setCountriesList(response);
        })
    }, []);

    console.log(countriesList)

    const countriesNames = [];
    const getCountriesNames = (countryName) => {
        countriesNames.push(countryName)
        return (countriesNames)
    }

    const countriesInfections = [];
    const getCountriesInfections = (countryInfections) => {
        countriesInfections.push(countryInfections)
        return (countriesInfections)
    }

    countriesList && countriesList.map((country) => getCountriesNames(country.name))
    countriesList && countriesList.map((country) => getCountriesInfections(country.infected))

    console.log(countriesNames)
    console.log(countriesInfections)

    return (
        <div style={{ width: '80%' }}>
            <h4 style={{ textAlign: 'center' }}>Chart</h4>
            <div style={{ height: '100vh', width: '100%', margin: 'auto' }}>
                <Pie
                    data={{
                        labels: countriesNames,
                        datasets: [
                            {
                                label: 'Contagios por pais',
                                data: [12, 19, 3, 5, 2, 3, 14, 8, 19, 5, 12, 19, 3, 5, 2, 3, 14, 8, 19, 5, 12, 19, 3, 5, 2, 3, 14, 8, 19, 5,],
                                backgroundColor: [
                                    'rgba(255, 99, 132, 0.2)',
                                    'rgba(54, 162, 235, 0.2)',
                                    'rgba(255, 206, 86, 0.2)',
                                    'rgba(75, 192, 192, 0.2)',
                                    'rgba(153, 102, 255, 0.2)',
                                    'rgba(255, 159, 64, 0.2)'
                                ],
                                borderColor: [
                                    'rgba(255, 99, 132, 1)',
                                    'rgba(54, 162, 235, 1)',
                                    'rgba(255, 206, 86, 1)',
                                    'rgba(75, 192, 192, 1)',
                                    'rgba(153, 102, 255, 1)',
                                    'rgba(255, 159, 64, 1)'
                                ],
                                borderWidth: 1
                            },
                        ]
                    }}
                    height={50}
                    width={100}
                    options={{
                        maintainAspectRatio: false,
                        scales: {
                            yAxes: [
                                {
                                    ticks: {
                                        beginAtZero: true,
                                    }
                                }
                            ]
                        }
                    }}
                />
            </div>
        </div >
    )
}

export { Chart }
