import React, { useEffect, useState } from "react";
import { Table } from 'react-bootstrap'
import { infected } from "../../../../api/infected";
import "./style.css";

const Infected = () => {

    const [infectedPeople, setInfectedPeople] = useState();

    useEffect(() => {
        infected.getInfected().then((response) => {
            setInfectedPeople(response);
        })
    }, []);

    console.log(infectedPeople)
    console.log(infectedPeople[0].infect_date)

    // const timeStamp = 1611214867768;
    // const dateVal = new Date(timeStamp).toLocaleDateString('en-US');
    // console.log(dateVal)

    return (
        <div>
            <h1>Infectados</h1>
            <Table striped bordered hover>
                <thead>
                    <tr>
                        <th>Fecha contagio</th>
                        <th>Nombre</th>
                        <th>Apellido</th>
                        <th>Edad</th>
                        <th>Pais</th>
                        <th>Genero</th>
                    </tr>
                </thead>
                <tbody>
                    {infectedPeople && infectedPeople.map((infected) => (
                        <tr key={infected.id}>
                            <td>{new Date(`${infected.infect_date * 100}`).toLocaleDateString('en-US')}</td>
                            <td>{infected.first_name}</td>
                            <td>{infected.last_name}</td>
                            <td>{infected.age}</td>
                            <td>{infected.country}</td>
                            <td>{infected.female ? "Femenino" : "Masculino"}</td>
                        </tr>
                    ))}
                </tbody>
            </Table>
        </div >
    )
}

export { Infected }
