import { api } from "./api"

const getInfected = async () => {
    const data = await api({
        method: "get",
        url: "/infected",
    });
    // console.log(data.data)
    return data.data;
};

const infectedPerson = {
    first_name: "",
    last_name: "",
    country: "",
    live: true,
    age: 0,
    female: false
}


const addInfected = async () => {
    await api({
        method: "POST",
        headers: {
            'Content-Type': 'Application/json'
        },
        url: "/infected",
        body: JSON.stringify(infectedPerson),
    })
}

export const infected = { getInfected, addInfected }
