import { api } from "./api"

const getInfected = async () => {
    const data = await api({
        method: "get",
        url: "/infected",
    });
    // console.log(data.data)
    return data.data;
};

const infectedPerson = { firstName, lastName, country, live, age, female }

const addInfected = async () => {
    await api({
        method: "POST",
        headers: {
            'Content-Type': 'Application/json'
        },
        url: "/infected",
        body: JSON.stringify(infectedPerson),
    })
}

export const infected = { getInfected, addInfected }
