import React, { useMemo } from 'react'
import { useTable } from 'react-table'

const BasicTable = ({ datos, columnas }) => {

    const getCurrentDate = () => {
        const newDate = new Date();
        const year = newDate.getFullYear();
        const month = newDate.getMonth() + 1;
        const day = newDate.getDate();
        const today = `${day} / ${month} / ${year}`;
        return (today)
    }

    const stringifyDate = (miliseconds) => {
        const completeDate = new Date(miliseconds * 1000);
        const year = completeDate.getFullYear();
        const month = completeDate.getMonth() + 1;
        const day = completeDate.getDate();
        const date = `${day} / ${month} / ${year}`;
        return (date)
    }

    // const newList = [];
    // const changeProps = () => {
    //     for (let obj of datos) {
    //         console.log(obj);
    //         console.log(obj.infect_date)
    //         obj.infect_date = stringifyDate(obj.infect_date);
    //         newList.push(obj);
    //     }
    //     console.log(newList)
    //     return (newList)
    // }
    // changeProps();
    // console.log(newList)

    //let gendre = '';
    const showGendre = () => {
        for (let obj of datos) {
            console.log(obj.female);
            if (obj.female) {
                return obj.female = 'Mujer';
            } else {
                return obj.female = 'Hombre';
            }
            //obj.female = obj.female ? 'Hombre' : 'Mujer';
            console.log(obj.infect_date)
            //obj.infect_date = stringifyDate(`${obj.infect_date}`);
        }
    }
    showGendre();

    const columns = useMemo(() => columnas, [])
    const data = useMemo(() => datos, [])

    const tableInstance = useTable({
        columns,
        data,
    })

    const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = tableInstance


    return (
        <div>
            <h3 style={{ backgroundColor: 'rgb(87, 234, 154)', textAlign: 'center', marginBottom: '5vh' }}>Cantidad de infectados al dia {getCurrentDate()}: {data.length}</h3>
            <table {...getTableProps()}>
                <thead>
                    {headerGroups.map((headerGroup) => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {
                                headerGroup.headers.map(column => (
                                    <th {...column.getHeaderProps()}>{column.render('Header')}</th>
                                ))
                            }
                        </tr>
                    ))}
                </thead>
                <tbody {...getTableBodyProps()}>
                    {
                        rows.map((row) => {
                            prepareRow(row)
                            return (
                                <tr {...row.getRowProps()}>
                                    {
                                        row.cells.map((cell) => {
                                            return <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                                        })
                                    }
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>
        </div>
    )
}

export { BasicTable }
