import React, { useEffect, useState } from "react";
import { Table, Button } from 'react-bootstrap'
import { TableExport } from 'tableexport'
import { infected } from "../../../../../api/infected";

const InfoTable = () => {

    const [infectedPeople, setInfectedPeople] = useState();

    useEffect(() => {
        infected.getInfected().then((response) => {
            setInfectedPeople(response);
        })
    }, []);

    console.log(infectedPeople)

    const totalInfected = infectedPeople && infectedPeople.length;

    const getDate = (miliseconds) => {
        const completeDate = new Date(miliseconds * 1000);
        const year = completeDate.getFullYear();
        const month = completeDate.getMonth() + 1;
        const day = completeDate.getDate();
        const date = `${day} / ${month} / ${year}`;
        return (date)
    }

    const newDate = new Date();
    const year = newDate.getFullYear();
    const month = newDate.getMonth() + 1;
    const day = newDate.getDate();
    const today = `${day} / ${month} / ${year}`;


    const loadTable = () => {
        return (infectedPeople && infectedPeople.map((infected) => (
            <tr key={infected.id} style={{ backgroundColor: `${infected.live ? "white" : "rgba(255, 99, 132, 0.4)"}` }}>
                <td>{getDate(`${infected.infect_date}`)}</td>
                <td>{infected.first_name}</td>
                <td>{infected.last_name}</td>
                <td>{infected.age}</td>
                <td>{infected.country}</td>
                <td>{infected.female ? "Femenino" : "Masculino"}</td>
            </tr>
        )))

    }

    const ordenar = () => {
        infectedPeople.sort(function (prev, next) {
            return prev.age - next.age
        })
        console.log(infectedPeople)
        loadTable();
    }

    const table = document.getElementById("dataTable")
    console.log(table)
    const exportTable = new TableExport(table, {
        exportButtons: false,
        filename: "Tabla de infectados COVID-19"
    });
    let datos = table.getExportData();
    let preferenciasDocumento = datos.table.xlsx;
    table.export2file(preferenciasDocumento.data, preferenciasDocumento.mimeType, preferenciasDocumento.filename, preferenciasDocumento.fileExtension, preferenciasDocumento.merges, preferenciasDocumento.RTL, preferenciasDocumento.sheetname);

    return (
        <div>
            <h3 style={{ backgroundColor: 'rgb(87, 234, 154)', textAlign: 'center', marginBottom: '5vh' }}>Cantidad de infectados al dia {today}: {totalInfected}</h3>
            <Button variant="success" onClick={exportTable}>
                Exportar tabla
            </Button>
            <div style={{ width: '80%', margin: 'auto' }}>
                <Table bordered hover>
                    <table id="dataTable">
                        <thead>
                            <tr>
                                <th>Fecha contagio</th>
                                <th>Nombre</th>
                                <th>Apellido</th>
                                <th>Edad<Button style={{ padding: '0', marginLeft: '10px' }} variant="link" onClick={() => ordenar()}>▼</Button></th>
                                <th>Pais</th>
                                <th>Genero</th>
                            </tr>
                        </thead>
                        <tbody>
                            {loadTable()}
                        </tbody>
                    </table>

                </Table>


            </div>
        </div>
    )
}

export { InfoTable }
